func removeHexPrefix(s string) string {
	return strings.TrimPrefix(s, "0x")
}

func toBytes(s string) []byte {
	n, err := hex.DecodeString(removeHexPrefix(s))
	if err != nil {
		panic(err)
	}
	return n
}

func mustRLPEncode(v interface{}) []byte {
	value, err := rlp.EncodeToBytes(v)
	if err != nil {
		panic(err)
	}
	return value
}

func toRLP(s string) []byte {
	return mustRLPEncode(toBytes(s))
}

func proofToBytes(proof []string) [][]byte {
	var r [][]byte
	for _, n := range proof {
		d, err := hex.DecodeString(removeHexPrefix(n))
		if err != nil {
			return [][]byte{}
		}
		r = append(r, d)
	}
	return r
}

